trigger.script:
/* Runtime-info Application: ProcessoNegocio Referer: http://erp.redectc.com.br:40024/workflow/ProcessoNegocio.xhtml5 ResourceID: br.com.sankhya.workflow.cad.processo.negocio service-name: ProcessoNegocioSP.importacao uri: /workflow/service.sbr */ CREATE TRIGGER AD_TRG_UPD_FLWPRO ON ${TABLE_NAME} FOR UPDATE AS BEGIN DECLARE @NUMROWS INT, @ERRMSG VARCHAR(255)
SELECT @NUMROWS = @@ROWCOUNT IF @NUMROWS = 0 RETURN;
IF (SANKHYA.STP_GET_ATUALIZANDO() = 1) RETURN
IF UPDATE(TGFPRO_CODGRUPOPROD) BEGIN IF ((SELECT COUNT(1) FROM INSERTED I, TGFGRU G WHERE ISNULL(I.TGFPRO_CODGRUPOPROD, 0) = G.CODGRUPOPROD AND G.ATIVO = 'S' AND G.ANALITICO = 'S') <> @NUMROWS) BEGIN EXEC STP_POPULA_MSG 'TGFGRU' GOTO ERROR END END ERROR: EXEC SANKHYA.SNK_ERROR @ERRMSG END;
